from web3 import Web3
from web3.auto import w3
import json
import random
import time

print('Telegram Channel - https://t.me/CryptoMindYep')
print()
wallets_folder = str(input('txt file format(address:privatekey): '))
time_sleep = input("press 'y' if need random time between transactions: ")
if time_sleep == 'y' or time_sleep == 'Y':
    t1 = int(input('enter min time in sec: '))
    t2 = int(input('enter max time in sec: '))

with open(wallets_folder, 'r') as file:
	wallets = [row.strip() for row in file]

